import React, { useState, useEffect } from 'react';
import Table from 'react-bootstrap/Table';
import Form from 'react-bootstrap/Form';

const Todos = () => {
    // Iniciando estado com array vazio.
    const [data, setData] = useState([]);
    // Sempre que o componente carregar, executar a função dentro do useEffect.
    useEffect(() => {
        async function getTodos() {
            // Montando requisição
            const response = await fetch('https://jsonplaceholder.typicode.com/todos').then(response => response.json())
            // Setando o estado data com os valores retornados pela requisição
            setData(response)
        }
        // Chamando função.
        getTodos();
    }, [])


    return(
        <>
        <Table striped bordered hover>
            <thead>
                <tr>
                <th>ID</th>
                <th>Titulo</th>
                <th>Completo</th>
                </tr>
            </thead>
            <tbody>
               {data.map((item, key) => (
                    <tr key={key}>
                    <td>{item.id}</td>
                    <td>{item.title}</td>
                    <td>
                        {item.completed === false ? (
                            <Form.Check 
                                type="switch"
                                id="custom-switch"
                                checked={false}
                            />
                        ):(
                            <Form.Check 
                                type="switch"
                                id="custom-switch"
                                checked={true}
                                feedbackTooltip={true}
                            />
                        )}
                   </td>

                    </tr>
               ))}
            </tbody>
        </Table>
        </>
    )
}
export default Todos;
